// class for doing everything we need to do to generate an executable ELF file
// 
ELFFormat::ELFFormat(std::string filenamearg) {
    std::cout << "Creating ELF file: " << filenamearg << std::endl;
    filename = filenamearg;
}

void ELFFormat::writeFile() {
    std::ofstream fs(filename+".o", std::ios::out | std::ios::binary | std::ios::app);
    fs.write((char*) &data, sizeof(data));
    fs.close();
}
